css:
/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  font-size: 1.5em;
  line-height: 1.4;
  padding: 4em;
  color: #adaaa4;
  background-color: #363430;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .icon::before {
  margin-right: 0;
  font-size: 1.5em;
  width: auto;
}
.about .about-container {
  width: 100%;
  max-width: 500px;
}
.about .about-header {
  text-align: center;
}
.about .about-header .about-atom-io:hover .about-logo {
  color: #40a977;
}
.about .about-header .about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 0.4em auto;
  color: #dddcda;
  transition: color 0.2s;
}
.about .about-header .about-heading {
  font-size: 1.5em;
  font-weight: bold;
}
.about .about-header .about-version-container:hover {
  color: #d1d0cc;
}
.about .about-header .about-version-container:active {
  color: #f5f5f4;
}
.about .about-header .about-version-container .about-version {
  margin-right: 8px;
}
.about .about-header .about-version-container .about-copy-version::before {
  position: relative;
  bottom: 1px;
}
.about .about-header button {
  font-size: 1em;
}
.about .about-header .icon::before {
  font-size: 1em;
}
.about .about-actions {
  margin-top: 3em;
  font-size: .86em;
  text-align: center;
}
.about .about-note {
  font-size: .86em;
  color: rgba(173, 170, 164, 0.6);
}
.about .about-metrics {
  margin-top: 2em;
}
.about .about-credits {
  margin-top: 40px;
  text-align: center;
}
.about .about-love {
  margin-top: 40px;
  text-align: center;
  font-size: .86em;
}
.about .about-love .icon::before {
  position: relative;
  top: 1px;
}
.about .about-love .icon-logo-github::before {
  top: 3px;
}
